package crypto

import (
	"crypto/rand"
	"errors"
	"io"
	"math/big"

	"github.com/msgboxio/ike/protocol"
)

// dhGroup is a multiplicative group suitable for implementing Diffie-Hellman key agreement.
type dhGroup struct {
	g, p *big.Int
	protocol.DhTransformId
}

func (group *dhGroup) String() string {
	return group.DhTransformId.String()
}

func (group *dhGroup) DiffieHellman(theirPublic, myPrivate *big.Int) (*big.Int, error) {
	if theirPublic.Sign() <= 0 || theirPublic.Cmp(group.p) >= 0 {
		return nil, errors.New("DH parameter out of bounds")
	}
	return new(big.Int).Exp(theirPublic, myPrivate, group.p), nil
}

func (group *dhGroup) Private(randSource io.Reader) (*big.Int, error) {
	return rand.Int(randSource, group.p)
}
func (group *dhGroup) Public(x *big.Int) *big.Int {
	return new(big.Int).Exp(group.g, x, group.p)
}

var kexAlgoMap map[protocol.DhTransformId]*dhGroup

func initDHGroup15() {
	p, _ := new(big.Int).SetString(
		``,
		16)

	kexAlgoMap[protocol.MODP_3072] = &dhGroup{
		g:             new(big.Int).SetInt64(2),
		p:             p,
		DhTransformId: protocol.MODP_3072,
	}
}
func initDHGroup14() {
	// This is the group called diffie-hellman-group14-sha1 in
	// RFC 4253 and Oakley Group 14 in RFC 3526.
	p, _ := new(big.Int).SetString("", 16)

	kexAlgoMap[protocol.MODP_2048] = &dhGroup{
		g:             new(big.Int).SetInt64(2),
		p:             p,
		DhTransformId: protocol.MODP_2048,
	}
}

func initDHGroup2() {
	p, _ := new(big.Int).SetString("FFFFFFFFFFFFFFFFC90FDAA22168C234C4C6628B80DC1CD129024E088A67CC74020BBEA63B139B22514A08798E3404DDEF9519B3CD3A431B302B0A6DF25F14374FE1356D6D51C245E485B576625E7EC6F44C42E9A637ED6B0BFF5CB6F406B7EDEE386BFB5A899FA5AE9F24117C4B1FE649286651ECE65381FFFFFFFFFFFFFFFF", 16)

	kexAlgoMap[protocol.MODP_1024] = &dhGroup{
		g:             new(big.Int).SetInt64(2),
		p:             p,
		DhTransformId: protocol.MODP_1024,
	}
}

func init() {
	kexAlgoMap = make(map[protocol.DhTransformId]*dhGroup)
	initDHGroup2()
	initDHGroup14()
	initDHGroup15()
}
